import s from "./Form.module.css";

import { Validations } from "./validations.js";

import { useState } from "react";

export default function Form({ login }) {
  // console.log(Validations)

  //conectar con los inputs

  const [userData, setUserData] = useState({
    email: "",
    password: "",
  });

  const [errors, setErrors] = useState({});

  function handleChange(event) {
    setUserData({
      ...userData,
      [event.target.name]: event.target.value,
    });
    // console.log(userData)

    setErrors(
      Validations({
        ...userData,
        [event.target.name]: event.target.value,
      })
    );
  }

  // function handleChange(event){
  //     console.log(event.target.name)
  // }

  function handleSubmit(event) {
    // console.log(event)
    event.preventDefault();
    login(userData);
  }

  return (
    <form className={s.p} onSubmit={handleSubmit}>
      <label className={s.labelX}>Email: </label>
      <input
        className={s.input}
        type="text"
        placeholder="Email..."
        name="email"
        // value={userData.email}
        onChange={handleChange}
      />
      <br />
      {errors.email ? <span className={s.errors}>{errors.email}</span> : null}
      {/* {errors.email&&<div>{errors.email}</div>} */}

      <br />
      <br />

      <label className={s.labelX}>Password: </label>
      <input
        className={s.input}
        type="text"
        placeholder="Password..."
        name="password"
        // value={userData.password}
        onChange={handleChange}
      />
      <br />
      {errors.password ? (
        <span className={s.errors}>{errors.password}</span>
      ) : null}

      <br />
      <br />

      <button
        className={s.button}
        type="submit"
        disabled={
          !userData.email ||
          !userData.password ||
          errors.email ||
          errors.password
        }
      >
        Submit
      </button>

      <input type="tel" />
    </form>
  );
}
